import React, { Component } from 'react';

class ElementVeriableRendering extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn : false
        }
    }

    render() {
        if(this.state.isLoggedIn) {
            return (
                <div>
                    <div>Welcome Azhar</div>
                </div>
            );
        } else {
            return (
                <div>
                    <div>Welcome Guest</div>                    
                </div>
            );
        }

    }
}

export default ElementVeriableRendering;
