import React, { Component } from 'react';
import ChildComponent from './ChildComponent';

class ParentComponent extends Component {

    constructor(props) {
        super(props);

        this.state = {
            parentName: 'Parent'
        }
        this.greetParent = this.greetParent.bind(this)
        this.greetParent = this.greetChild.bind(this)
    }

    greetParent() {
        alert(`Hello ${this.state.parentName}`)
    }

    greetChild() {
        alert(`Hello ${this.state.parentName} from ${this.props.children}`)
    }

    render() {
        return (
            <div>
                <ChildComponent greetParentHandler={this.greetParent} greetChildHandler={this.greetChild}/>
                <ChildComponent greetChildHandler={this.greetChild}/>
            </div>
        );
    }
}

export default ParentComponent;
