import React, { Component } from 'react';

class FormComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            userName : ''
        }
    }

    render() {
        return (
            <form>
                <div>
                    <label>User Name:</label>        
                    <input type="text" value={this.state.userName}/>
                </div>
            </form>
        );
    }
}

export default FormComponent;
