import React, { Component } from 'react';
import axios from 'axios';
class HttpGET extends Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: [],
            errorMsg: ''
        }
    }

    // it will be executed once during component lifetime
    componentDidMount() {
        axios.get("https://jsonplaceholder.typicode.com/posts")
        .then(response => {
            console.log(response)
            this.setState({
                posts: response.data
            })
        })
        .catch(error =>{
            console.log(error)
            this.setState({
                errorMsg: 'Invalid URL while retreving data'
            });
        })

    }

    render() {
        const {posts,errorMsg} = this.state
        return (
            <div>
                List of Post:
                {
                    posts.length ?
                    posts.map(post => <div key={post.id}>{post.title} </div>): 
                    null
                }
                {
                    errorMsg ? <div>{errorMsg}</div>: null
                }
            </div>
        );
    }
}

export default PostList;
